import React, { Component } from "react";
import CardColumns from "react-bootstrap/CardColumns";
import PostContainer from "./PostContainer";

class PostsContainer extends Component {
  state = {
    posts: [
      { title: "Grass Cutting", description: "Cut Grass will pay good $$" },
      { title: "Grave Digging", description: "Dig Grave" },
      { title: "Fry Cook", description: "Fry burgers plzzz" },
      { title: "Good Cop", description: "Be a good cop" },
      { title: "Bad Cop", description: "Be a bad cop" },
      { title: "Interogee", description: "Be interogated" },
    ],
  };

  render() {
    return (
      <div>
        <center>
          <h3> Popular Posts </h3>
          <CardColumns>
            {this.state.posts.map((post) => (
              <PostContainer title={post.title}></PostContainer>
            ))}
          </CardColumns>
        </center>
      </div>
    );
  }
}

export default PostsContainer;
